import React, { useState } from "react";
import { YMaps, Map, Placemark} from '@pbe/react-yandex-maps';

const MapLocalComponent = ({a,b}) => {
  const [coordinates] = useState([a, b]); 
  const [isPointVisible, setIsPointVisible] = useState(true); 

  const togglePoint = () => {
    setIsPointVisible(!isPointVisible); 
  }
  return (
    <YMaps>
      <div>
        <Map
          defaultState={{
            center: coordinates, 
            zoom: 10,
          }}
          width="100%"
          height="400px"
        >
          {isPointVisible && (
            <Placemark
              geometry={coordinates} 
              
              options={{
                iconLayout: "default#image",
                iconImageHref: "https://cdn-icons-png.flaticon.com/512/684/684908.png",
                iconImageSize: [30, 30],
                iconImageOffset: [-15, -30],
              }}
            />
          )}
        </Map>
      </div>
    </YMaps>
  );
};

export default MapLocalComponent;
