import React from 'react';
import Container from './Container';
import classes from './Header.module.sass';
import {Link} from "react-router-dom";
import { ReactComponent as Logo} from "../images/icon-logoWhiteText.svg"
import { ReactComponent as World} from "../images/icon-world.svg"
import { useDispatch , useSelector } from 'react-redux';
import { setIsLang, setLang } from '../redux/HeaderSlice';
import { useNavigate } from "react-router-dom"
const Header = () => {
    const {lang, isLang} = useSelector(state => state.HeaderReducer)
    const dispatch = useDispatch()
    console.log(isLang);
    console.log(lang);
    return (
        <div className={classes.Header}>
            <Container inner = {
                <div className={classes.Header__inner}>
                    <Logo className={classes.Header__inner__logo}/>
                    <div className={classes.Header__inner__line_R}/>
                    <div className={classes.Header__inner__line_L}/>
                    <button className={classes.Header__inner__button}>Забронировать</button>
                    <div className={classes.Header__inner__translate}>
                        <div onClick={()=>{dispatch(setIsLang())}} className={classes.Header__inner__translate__active}>
                            <World className={classes.Header__inner__translate__active__icon}/>
                            <p className={classes.Header__inner__translate__active__var}>RU</p>
                        </div>
                        <p onClick={()=>{dispatch(setLang("EN"))}} className={isLang?"var_on":"var_off"}>EN</p>
                        <p onClick={()=>{dispatch(setLang("KG"))}} className={isLang?"var_on":"var_off"}>KG</p>
                    </div>
                </div>
            }/>
        </div>
    );
};

export default Header;
