import { createSlice } from '@reduxjs/toolkit';

const HeaderSlice = createSlice({
    name: "postSlice",
    initialState: {
        lang: "RU",
        Islang: "RU"
    },
    reducers: {
        setLang:(state,action)=>{state.lang = action.payload}
    },
});

export const {setLang} = HeaderSlice.actions
export default HeaderSlice.reducer;
