import React from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import { Navigation } from "swiper/modules";
import "swiper/css";
import "swiper/css/navigation";

const items = [
  { id: 1, title: "Карточка 1", color: "bg-red-500" },
  { id: 2, title: "Карточка 2", color: "bg-blue-500" },
  { id: 3, title: "Карточка 3", color: "bg-green-500" },
  { id: 4, title: "Карточка 4", color: "bg-yellow-500" },
];

const Carousel = ({photos}) => {
  return (
    <div className="w-full max-w-lg mx-auto relative">
      <Swiper
        modules={[Navigation]}
        navigation
        loop={true}
        spaceBetween={0}
        slidesPerView={2}
        className="Carousel"
      >
        {photos.map((item) => (
          <SwiperSlide key={item.id}>
            <div className="Carousel__card" style={{backgroundImage: `url(${item.img})`}}>
              {item.title}
            </div>
          </SwiperSlide>
        ))}
      </Swiper>
    </div>
  );
};

export default Carousel;
